import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

import tmdbApi from '../../api/tmdbApi';
import apiConfig from '../../api/apiConfig';

import './detail.scss';
import CastList from './CastList';
import VideoList from '../../components/video-list/VideoList';

import MovieButton from '../../components/movie-button/MovieButton';

const Detail = () => {

    const { category, id } = useParams();

    const [item, setItem] = useState(null);

    useEffect(() => {
        const getDetail = async () => {
            const response = await tmdbApi.detail(category, id, { params: {} });
            setItem(response);
            window.scrollTo(0, 0);
        }
        getDetail();
    }, [category, id]);

    return (
        <>
            {
                item && (
                    <>
                        <div className="banner" style={{ backgroundImage: `url(${apiConfig.originalImage(item.backdrop_path || item.poster_path || item.profile_path)})` }}></div>
                        <div className="mb-3 movie-content container">
                            <div className="movie-content__poster">
                                <div className="movie-content__poster__img" style={{ backgroundImage: `url(${apiConfig.originalImage(item.poster_path || item.backdrop_path || item.profile_path)})` }}></div>
                            </div>
                            <div className="movie-content__info">
                                <h1 className="title">
                                    {item.title || item.name}
                                </h1>
                                <div className="genres">
                                    {
                                        item.genres && item.genres.slice(0, 5).map((genre, i) => (
                                            <span key={i} className="genres__item">{genre.name}</span>
                                        ))
                                    }
                                </div>
                                <p className="overview">{item.overview || item.biography}</p>
                                <div className="cast">
                                    <div className="section__header">
                                        <h2>Participation</h2>
                                    </div>
                                    <CastList id={item.id} />
                                    <div className="section mb-3">
                                        <h2>RELEASE DATE</h2>
                                        <p>{item.release_date}</p>

                                        <p style={{ color: "#5a606b", fontWeight: "bolder" }}>RUN TIME</p>
                                        <p style={{ color: "#f4c10f" }}>{item.runtime}</p>

                                        <p style={{ color: "#5a606b", fontWeight: "bolder" }}>BUDGET</p>
                                        <p style={{ color: "#f4c10f" }}>{item.budget}</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="container">
                            <div className="section mb-3">
                                <VideoList id={item.id} />
                            </div>
                            <div className="section mb-3">
                                <div className="section__header mb-2">
                                </div>
                                <MovieButton category={category} type="similar" id={item.id} />
                            </div>

                        </div>
                    </>
                )
            }
        </>
    );
}

export default Detail;
