import AxiosUser from "./AxiosUser";

export const id = {
    movie: 'movie',
    tv: 'tv',
    person : 'person'
}

export const movie = {
    upcoming: 'upcoming',
    popular: 'popular',
    top_rated: 'top_rated'
}

export const tv = {
    popular: 'popular',
    top_rated: 'top_rated',
    on_the_air: 'on_the_air'

}
export const person = {
    popular: 'popular',
    movie_credits: 'movie_credits'
}

const ConfigApi = {
    getMoviesList: (id, params) => {
        const link = 'movie/' + movie[id];
        return AxiosUser.get(link, params);
    },
    getTvList: (id, params) => {
        const link = 'tv/' + tv[id];
        return AxiosUser.get(link, params);
    },
    getPersonList: (id, params) => {
        const link = 'person/' + person[id];
        return AxiosUser.get(link, params);
    },
    getVideos: (type, id) => {
        const link = id[type] + '/' + id + '/videos';
        return AxiosUser.get(link, {params: {}});
    },
    
    search: (type, params) => {
        const link = 'search/' + id[type];
        return AxiosUser.get(link, params);
    },
    type: (type, id, params) => {
        const link = id[type] + '/' + id;
        return AxiosUser.get(link, params);
    },
    credits: (type, id) => {
        const link = id[type] + '/' + id + '/credits';
        return AxiosUser.get(link, {params: {}});
    },
    
    similar: (type, id) => {
        const link = id[type] + '/' + id + '/similar';
        return AxiosUser.get(link, {params: {}});
    }
   
}

export default ConfigApi;
