import React, { useState, useEffect } from 'react';

import { useParams } from 'react-router';

import tmdbApi from '../../api/tmdbApi';
import apiConfig from '../../api/apiConfig';

const CastList = props => {
    const item  = props.item;

    const {category} = useParams();

    const bg = apiConfig.w500Image(item.poster_path || item.backdrop_path);

    const [casts, setCasts] = useState([]);

    useEffect(() => {
        const getCredits = async () => {
            const res = await tmdbApi.credits(category, props.id);
            setCasts(res.cast.slice(0, 5));
        }
        getCredits();
    }, [category, props.id]);
    return (
        <div className="casts">
            {
                casts.map((item, imagen) => (
                    <div key={imagen} className="casts__item">
                        <div className="casts__item__img" style={{backgroundImage: `url(${apiConfig.w500Image(item.profile_path || bg )})`}}></div>
                        <p className="casts__item__name">{item.title || item.name}</p>
                    </div>
                ))
            }
        </div>
    );
}

export default CastList;
